mapper.select:
/* getListDataLibraryGroup */ WITH RECURSIVE data_library_group_tree ( data_library_group_id, data_library_group_name, data_library_group_key, data_library_group_path, data_library_group_target, sharing, user_id, ancestor, parent, depth, view_order, children, basic, available, data_library_count, description, update_date, insert_date, path, cycle ) AS ( SELECT A.data_library_group_id, A.data_library_group_name, A.data_library_group_key, A.data_library_group_path, A.data_library_group_target, A.sharing, A.user_id, A.ancestor, A.parent, A.depth, A.view_order, A.children, A.basic, A.available, A.data_library_count, A.description, A.update_date, A.insert_date, ARRAY[A.view_order], false FROM data_library_group A WHERE A.parent = 0 AND A.data_library_group_target = 'admin'
AND A.available = #{available}
UNION ALL SELECT A.data_library_group_id, A.data_library_group_name, A.data_library_group_key, A.data_library_group_path, A.data_library_group_target, A.sharing, A.user_id, A.ancestor, A.parent, A.depth, A.view_order, A.children, A.basic, A.available, A.data_library_count, A.description, A.update_date, A.insert_date, path || A.view_order, A.data_library_group_id = ANY(path) FROM data_library_group A, data_library_group_tree B WHERE A.parent = B.data_library_group_id AND not cycle ) SELECT data_library_group_id, data_library_group_name, data_library_group_key, data_library_group_path, data_library_group_target, sharing, user_id, ancestor, parent, depth, view_order, children, basic, available, data_library_count, description, update_date, insert_date, path FROM data_library_group_tree ORDER BY path

/* getDataLibraryGroup */
SELECT A.*,
	CASE 
		WHEN parent > 0 THEN ( SELECT data_library_group_name FROM data_library_group WHERE data_library_group_id = A.parent )
		ELSE null
	END AS parentName
FROM data_library_group A
WHERE A.data_library_group_id = #{dataLibraryGroupId}

	AND A.user_id = #{userId}

/* getDataLibraryGroupByParentAndViewOrder */
SELECT *
FROM data_library_group
WHERE parent = #{parent}


	AND view_order < #{viewOrder}

ORDER BY view_order DESC



	AND view_order > #{viewOrder}

ORDER BY view_order

LIMIT 1

/* isDataLibraryGroupKeyDuplication */
SELECT 
	CASE 
		WHEN COUNT(*) > 0 THEN true
		ELSE false
	END AS duplication
FROM data_library_group
WHERE data_library_group_key = #{dataLibraryGroupKey}
	AND data_library_group_target = 'admin'

	AND user_id = #{userId}

/* getChildrenDataLibraryGroupListByParent */
SELECT *
FROM data_library_group
WHERE parent = #{dataLibraryGroupId}

	AND user_id = #{userId}

mapper.insert:
/* insertBasicDataLibraryGroup */

	SELECT NEXTVAL('data_library_group_seq')

INSERT INTO data_library_group (
	data_library_group_id, data_library_group_name, data_library_group_key, data_library_group_path, data_library_group_target,
	sharing, user_id, ancestor, parent, depth, view_order, children, basic, available, data_library_count, description
) VALUES (
	#{dataGroupId}, #{dataGroupName}, #{dataGroupKey}, #{dataGroupPath}, #{dataGroupTarget},
	 #{sharing}, #{userId}, {dataLibraryGroupId}, 0, 1, 1, 0, true, true, 0, trim(#{description})
)

mapper.update:
/* updateDataLibraryGroupViewOrder */
UPDATE data_library_group
SET view_order = #{viewOrder}
WHERE data_library_group_id = #{dataLibraryGroupId}

	AND user_id = #{userId}

/* updateDataLibraryGroupChildren */
UPDATE data_library_group
SET children = children + #{children}
WHERE data_library_group_id = #{dataLibraryGroupId}

	AND user_id = #{userId}

mapper.delete:
/* deleteDataLibraryGroupByAncestor */
DELETE
FROM data_library_group
WHERE ancestor = #{dataLibraryGroupId}
	OR data_library_group_id = #{dataLibraryGroupId}

	AND user_id = #{userId}

/* deleteDataLibraryGroupByParent */
DELETE
FROM data_library_group
WHERE parent = #{dataLibraryGroupId}
	OR data_library_group_id = #{dataLibraryGroupId}

	AND user_id = #{userId}

/* deleteDataLibraryGroup */
DELETE
FROM data_library_group
WHERE data_library_group_id = #{dataLibraryGroupId}

	AND user_id = #{userId}

/* deleteDataLibraryGroupByUserId */
DELETE
FROM data_library_group
WHERE user_id = #{value}
