mapper.select:
/* getDataLibraryGroupTotalCount */
SELECT COUNT(data_library_group_id)
FROM data_library_group


		user_id = #{userId}


	AND data_library_group_id = #{dataLibraryGroupId}


	AND sharing = #{sharing}


	AND basic = #{basic}


	AND ${searchWord} = #{searchValue}


	AND ${searchWord} LIKE '%' || #{searchValue} || '%'



	AND insert_date >= TO_TIMESTAMP(#{startDate}, 'YYYYMMDDHH24MISSUS')




	AND insert_date <= TO_TIMESTAMP(#{endDate}, 'YYYYMMDDHH24MISSUS')

/* getListLayerGroup */ WITH RECURSIVE data_library_group_tree ( data_library_group_id, data_library_group_name, data_library_group_key, data_library_group_path, data_library_group_target, sharing, user_id, ancestor, parent, depth, view_order, children, basic, available, tiling, data_count, location, altitude, duration, location_update_type, metainfo, description, update_date, insert_date, path, cycle ) AS ( SELECT A.data_library_group_id, A.data_library_group_name, A.data_library_group_key, A.data_library_group_path, A.data_library_group_target, A.sharing, A.user_id, A.ancestor, A.parent, A.depth, A.view_order, A.children, A.basic, A.available, A.tiling, A.data_count, A.location, A.altitude, A.duration, A.location_update_type, A.metainfo, A.description, A.update_date, A.insert_date, ARRAY[A.view_order], false FROM data_library_group A WHERE A.parent = 0 AND A.data_library_group_target = 'admin'
AND A.available = #{available}
UNION ALL SELECT A.data_library_group_id, A.data_library_group_name, A.data_library_group_key, A.data_library_group_path, A.data_library_group_target, A.sharing, A.user_id, A.ancestor, A.parent, A.depth, A.view_order, A.children, A.basic, A.available, A.tiling, A.data_count, A.location, A.altitude, A.duration, A.location_update_type, A.metainfo, A.description, A.update_date, A.insert_date, path || A.view_order, A.data_library_group_id = ANY(path) FROM data_library_group A, data_library_group_tree B WHERE A.parent = B.data_library_group_id AND not cycle ) SELECT data_library_group_id, data_library_group_name, data_library_group_key, data_library_group_path, data_library_group_target, sharing, user_id, ancestor, parent, depth, view_order, children, basic, available, tiling, data_count, location, altitude, duration, location_update_type, metainfo, description, update_date, insert_date, path FROM data_library_group_tree ORDER BY path

/* getDataLibraryGroup */
SELECT A.*, ST_X(A.location) AS longitude, ST_Y(A.location) AS latitude,
	CASE 
		WHEN parent > 0 THEN ( SELECT data_library_group_name FROM data_library_group WHERE data_library_group_id = A.parent )
		ELSE null
	END AS parentName
FROM data_library_group A
WHERE A.data_library_group_id = #{dataLibraryGroupId}

	AND A.user_id = #{userId}

/* getUserGroupByParentAndViewOrder */
SELECT *
FROM data_library_group
WHERE parent = #{parent}


	AND view_order < #{viewOrder}

ORDER BY view_order DESC



	AND view_order > #{viewOrder}

ORDER BY view_order

LIMIT 1

/* isDataLibraryGroupKeyDuplication */
SELECT 
	CASE 
		WHEN COUNT(*) > 0 THEN true
		ELSE false
	END AS duplication
FROM data_library_group
WHERE data_library_group_key = #{dataLibraryGroupKey}
	AND data_library_group_target = 'admin'

	AND user_id = #{userId}

/* getDataLibraryGroupListByAncestor */
SELECT data_library_group_id
FROM data_library_group
WHERE ancestor = #{ancestor}

	AND user_id = #{userId}

/* getDataLibraryGroupListByParent */
SELECT data_library_group_id
FROM data_library_group
WHERE parent = #{ancestor}

	AND user_id = #{userId}

mapper.update:
/* updateDataLibraryGroupViewOrder */
UPDATE data_library_group
SET view_order = #{viewOrder}
WHERE data_library_group_id = #{dataLibraryGroupId}

	AND user_id = #{userId}

mapper.delete:
/* deleteDataLibraryGroupByAncestor */
DELETE
FROM data_library_group
WHERE ancestor = #{dataLibraryGroupId}
	OR data_library_group_id = #{dataLibraryGroupId}

	AND user_id = #{userId}

/* deleteDataLibraryGroupByParent */
DELETE
FROM data_library_group
WHERE parent = #{dataLibraryGroupId}
	OR data_library_group_id = #{dataLibraryGroupId}

	AND user_id = #{userId}

/* deleteDataLibraryGroup */
DELETE
FROM data_library_group
WHERE data_library_group_id = #{dataLibraryGroupId}

	AND user_id = #{userId}

/* deleteDataLibraryGroup */
DELETE
FROM data_library_group
WHERE user_id = #{value}
