mapper.select:
/* getListDesignLayerGroup */ WITH RECURSIVE design_layer_group_tree ( design_layer_group_id, design_layer_group_name, user_id, ancestor, parent, depth, view_order, children, available, description, insert_date, path, cycle ) AS ( SELECT A.design_layer_group_id, A.design_layer_group_name, A.user_id, A.ancestor, A.parent, A.depth, A.view_order, A.children, A.available, A.description, A.insert_date, ARRAY[A.view_order], false FROM design_layer_group A WHERE A.parent = 0
AND A.available = #{available}
UNION ALL SELECT A.design_layer_group_id, A.design_layer_group_name, A.user_id, A.ancestor, A.parent, A.depth, A.view_order, A.children, A.available, A.description, A.insert_date, path || A.view_order, A.design_layer_group_id = ANY(path) FROM design_layer_group A, design_layer_group_tree B WHERE A.parent = B.design_layer_group_id AND not cycle ) SELECT design_layer_group_id, design_layer_group_name, user_id, ancestor, parent, depth, view_order, children, available, description, insert_date, path FROM design_layer_group_tree ORDER BY path

/* getDesignLayerGroupGroupByParentAndViewOrder */
SELECT *
FROM design_layer_group
WHERE parent = #{parent}


	AND view_order < #{viewOrder}

ORDER BY view_order DESC



	AND view_order > #{viewOrder}

ORDER BY view_order

LIMIT 1

mapper.insert:
		/* insertDesignLayerGroup */

    		SELECT NEXTVAL('design_layer_group_seq')

		INSERT INTO design_layer_group (
			design_layer_group_id, design_layer_group_name, user_id, ancestor, parent, depth, view_order, children, available, description
		) VALUES (
			#{designLayerGroupId}, #{designLayerGroupName}, #{userId},
			(
				SELECT COALESCE(MAX(ancestor), #{designLayerGroupId})
				FROM design_layer_group
				WHERE design_layer_group_id = #{parent}
					AND design_layer_group_id != 0
			),
			#{parent},
			(
				SELECT COALESCE(MAX(depth),  0 ) + 1
				FROM design_layer_group
				WHERE design_layer_group_id = #{parent}
			),
			(
				SELECT COALESCE(MAX(view_order), 0) + 1
				FROM design_layer_group
				WHERE parent = #{parent}
			),
			(
				SELECT COUNT(*)
				FROM design_layer_group
				WHERE parent = #{designLayerGroupId}
			),
			#{available},
			trim(#{description})
		)

mapper.update:
/* updateDesignLayerGroupViewOrder */
UPDATE design_layer_group
SET view_order = #{viewOrder}
WHERE design_layer_group_id = #{designLayerGroupId}

mapper.delete:
/* deleteDesignLayerGroup */
DELETE
FROM design_layer_group
WHERE design_layer_group_id = #{designLayerGroupId}

	AND user_id = #{userId}

/* deleteDesignLayerGroupByAncestor */
DELETE
FROM design_layer_group
WHERE ancestor = #{designLayerGroupId}
OR design_layer_group_id = #{designLayerGroupId}

	AND user_id = #{userId}

/* deleteDesignLayerGroupByParent */
DELETE
FROM design_layer_group
WHERE parent = #{designLayerGroupId}
OR design_layer_group_id = #{designLayerGroupId}

	AND user_id = #{userId}
