mapper.select:
/* getListDesignLayerGroup */ WITH RECURSIVE design_layer_group_tree ( design_layer_group_id, design_layer_group_name, user_id, ancestor, parent, depth, view_order, children, available, description, insert_date, path, cycle ) AS ( SELECT A.design_layer_group_id, A.design_layer_group_name, A.user_id, A.ancestor, A.parent, A.depth, A.view_order, A.children, A.available, A.description, A.insert_date, ARRAY[A.view_order], false FROM design_layer_group A WHERE A.available = true
AND A.design_layer_group_id = #{designLayerGrouupId}
AND A.parent = 0
UNION ALL SELECT A.design_layer_group_id, A.design_layer_group_name, A.user_id, A.ancestor, A.parent, A.depth, A.view_order, A.children, A.available, A.description, A.insert_date, path || A.view_order, A.design_layer_group_id = ANY(path) FROM design_layer_group A, design_layer_group_tree B WHERE A.parent = B.design_layer_group_id AND not cycle ) SELECT design_layer_group_id, design_layer_group_name, user_id, ancestor, parent, depth, view_order, children, available, description, insert_date, path FROM design_layer_group_tree
WHERE design_layer_group_id != #{designLayerGrouupId}
ORDER BY path

/* getDesignLayerGroupGroupByParentAndViewOrder */
SELECT *
FROM design_layer_group
WHERE parent = #{parent}


	AND view_order < #{viewOrder}

ORDER BY view_order DESC



	AND view_order > #{viewOrder}

ORDER BY view_order

LIMIT 1
